import React, { useEffect, useState } from "react";
import classes from "../styles/HeaderBurger.module.css";
import NavigationBurger from "../components/NavigationBurger";
import Footer from "../components/Footer";
import ProductItems from "../components/ProductItems";
import axios from "axios";
import { useLocation } from "react-router-dom";

function Jewelry() {
  const [item, setItem] = useState([]);
  const [brand, setBrand] = useState([]);
  const location = useLocation();
  console.log(location.state);
  const [fromLink, setFromLink] = useState("add");

  // useEffect(() => {
  //   axios
  //     .get(process.env.REACT_APP_API_URL + "api/jewelry")
  //     .then((response) => setItem(response.data));
  // }, []);
  useEffect(() => {
    async function fetchData() {
      const response = await fetch(
        process.env.REACT_APP_API_URL + "api/jewelry"
      );
      const data = await response.json();
      setItem(data);
    }

    fetchData();
  }, []);
  useEffect(() => {
    async function fetchData() {
      const response = await fetch(process.env.REACT_APP_API_URL + "api/brand");
      const data = await response.json();
      setBrand(data);
    }

    fetchData();
  }, []);

  return (
    <div className={classes.Jewelry}>
      <NavigationBurger></NavigationBurger>
      <p style={{ borderTop: "2px solid #fff", margin: "30px 162px" }}></p>
      <div className={classes.colect}>
        {fromLink === "add" ? (
          <div className={classes.widthItem}>
            {item.map((obj) => {
              return (
                <ProductItems
                  className={classes.productItemColumns}
                  name={obj.name}
                  brand={brand[obj.brandId - 1].name}
                  title={obj.title}
                  price={obj.price}
                  image={`jewelryPng/${obj.img}`}
                ></ProductItems>
              );
            })}
          </div>
        ) : fromLink === "wom" ? (
          <div className={classes.widthItem}>
            {item.map((obj) => {
              if (obj.form === "wom") {
                return (
                  <ProductItems
                    className={classes.productItemColumns}
                    name={obj.name}
                    brand={brand[obj.brandId - 1].name}
                    title={obj.title}
                    price={obj.price}
                    image={`jewelryPng/${obj.img}`}
                  ></ProductItems>
                );
              }
            })}
          </div>
        ) : fromLink === "man" ? (
          <div className={classes.widthItem}>
            {item.map((obj) => {
              if (obj.form === "man") {
                return (
                  <ProductItems
                    className={classes.productItemColumns}
                    name={obj.name}
                    brand={brand[obj.brandId - 1].name}
                    title={obj.title}
                    price={obj.price}
                    image={obj.img}
                  ></ProductItems>
                );
              }
            })}
          </div>
        ) : (
          <div>
            {item.map((obj) => {
              if (brand[obj.brandId - 1].name === fromLink) {
                return (
                  <ProductItems
                    className={classes.productItemColumns}
                    name={obj.name}
                    brand={brand[obj.brandId - 1].name}
                    title={obj.title}
                    price={obj.price}
                    image={obj.img}
                  ></ProductItems>
                );
              }
            })}
          </div>
        )}
      </div>
      <Footer></Footer>
    </div>
  );
}

export default Jewelry;
