import React, { useState } from 'react';
import classes from "../styles/HeaderBurger.module.css";
import Navigation from "./Navigation";
import logoWhite from "../img/logoW.png";
import IconBurger from "../img/x.png";
import { useDispatch } from "react-redux";
import { activeBorgerAction } from "../store/Burger";
import { NavLink } from "react-router-dom";
import Yuovelir from "./Yuovelir";
import Clock from './Clock';

const HeaderBurger = () => {
  const dispatch = useDispatch();
  const activeBurger = () => {
    dispatch(activeBorgerAction(true));
  };


  const [activPage, setActivPage] = useState()
  const [activClock, setActivClock] = useState()


  const actFun = () => {
    if (activPage === true){
      setActivPage(false)
    }else{
      setActivPage(true)
    }
  }

  const actClock = () => {
    if (activClock === true){
      setActivClock(false)
    }else{
      setActivClock(true)
    }
  }
  return (
    <div>
      <div className={classes.main}>
        <div className={classes.content}>
          <div className={classes.wth}>
            <Navigation
              icon={logoWhite}
              iconBurger={IconBurger}
              func={activeBurger}
            ></Navigation>
          </div>
          <div className={classes.pages}>
            <div>
                <NavLink
                  className={({ isActive }) => (isActive ? classes.active : "")}
                  onClick={() => {actFun()}}
                >
                  Ювелирные изделия
                </NavLink>
            </div>
            <div className={classes.act}
            >
              <NavLink
                className={({ isActive }) => (isActive ? classes.active : "")}
                onClick={() => {actClock()}}
              >
                часы
              </NavLink>
            </div>
            <div>
              <NavLink
                className={({ isActive }) => (isActive ? classes.active : "")}
              >
                бренды
              </NavLink>
            </div>
            <div>
              <NavLink
                className={({ isActive }) => (isActive ? classes.active : "")}
              >
                ароматы
              </NavLink>
            </div>
            <div className={classes.act}>
              <NavLink
                className={({ isActive }) => (isActive ? classes.active : "")}
              >
                изделия из кожи
              </NavLink>
            </div>
          </div>
          {activPage === true ? <Yuovelir></Yuovelir> : ""}
          {activClock === true ? <Clock></Clock> : ""}
        </div>
      </div>
    </div>
  );
};

export default HeaderBurger;
