const defaultState = {
  name: "",
};

const REGIST = "REGIST";

export const UserName = (state = defaultState, action) => {
  switch (action.type) {
    case REGIST:
      return { ...state, name: action.payload };
    default:
      return state;
  }
};

export const UserNameAction = (payload) => ({ type: REGIST, payload });
