import React, { useEffect, useState, Component } from "react";
import classes from "../styles/HeaderBurger.module.css";
import NavigationBurger from "../components/NavigationBurger";
import Footer from "../components/Footer";
import ProductItems from "../components/ProductItems";
import axios from "axios";
import { useLocation } from "react-router-dom";

function Clock() {
  const [item, setItem] = useState([]);
  const [brand, setBrand] = useState([]);
  const location = useLocation();
  let formLink = "wom";
  useEffect(() => {
    axios
      .get(process.env.REACT_APP_API_URL + "api/clock")
      .then((response) => setItem(response.data));
  }, []);
  useEffect(() => {
    axios
      .get(process.env.REACT_APP_API_URL + "api/brand")
      .then((response) => setBrand(response.data));
  }, []);

  return (
    <div className={classes.Jewelry}>
      <NavigationBurger></NavigationBurger>
      <p style={{ borderTop: "2px solid #fff", margin: "30px 162px" }}></p>
      {setTimeout(() => {
        <div className={classes.colect}>
          {formLink === "wom" ? (
            <div className={classes.widthItem}>
              {item.map((obj, index) => {
                if (obj.form === "wom") {
                  return (
                    <ProductItems
                      className={classes.productItemColumns}
                      name={obj.name}
                      brand={brand[obj.brandId - 1].name}
                      title={obj.title}
                      price={obj.price}
                      image={`clockPng/${obj.img}`}
                      timeout={1000 * index}
                    ></ProductItems>
                  );
                }
              })}
              );
            </div>
          ) : formLink === "man" ? (
            <div className={classes.widthItem}>
              {item.map((obj) => {
                if (obj.form === "man") {
                  return (
                    <ProductItems
                      className={classes.productItemColumns}
                      name={obj.name}
                      brand={brand[obj.brandId - 1].name}
                      title={obj.title}
                      price={obj.price}
                      image={obj.img}
                    ></ProductItems>
                  );
                }
              })}
            </div>
          ) : (
            <div>
              {item.map((obj) => {
                if (brand[obj.brandId - 1].name === formLink) {
                  return (
                    <ProductItems
                      className={classes.productItemColumns}
                      name={obj.name}
                      brand={brand[obj.brandId - 1].name}
                      title={obj.title}
                      price={obj.price}
                      image={obj.img}
                    ></ProductItems>
                  );
                }
              })}
            </div>
          )}
        </div>;
      }, 5000)}
      <Footer></Footer>
    </div>
  );
}
export default Clock;
