import classes from "../../styles/HeaderBurger.module.css";
import {NavLink, useLocation} from "react-router-dom";
import React, {useEffect, useState} from "react";
import { useDispatch } from "react-redux";
import { activeRegistAction } from "../../store/Burger";
import {login, registration} from "../../http/userAPI";
import {LOGIN_ROUTE} from "../../utils/consts";

function Registration() {
  const location = useLocation();
  const isLogin = location.pathname === LOGIN_ROUTE;
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const dispatch = useDispatch();
  const activeRegist = () => {
    dispatch(activeRegistAction(false));
  };

  const click = async () => {
    try {
      let data;
      if (!isLogin) {
        data = await registration(email, password);
      }
    } catch (e) {
      alert(e.response.data.message);
    }
  };

  return (
    <div className={classes.registForm}>
      <form className={classes.form} action="" method="post">
        <h1>РЕГИСТРАЦИЯ</h1>
        <input
          className={classes.input}
          type="email"
          placeholder="Ваш e-mail"
        />
        <input className={classes.input} type="password" placeholder="Пароль" />
        <button className={classes.btn} type="submit">
          Регистрация
        </button>
        <div style={{ marginTop: "15px" }}>
          <div style={{ float: "left", marginRight: "5px" }}>
            Есть аккаунта?
          </div>
          <NavLink
            style={{ float: "left", color: "#272727" }}
            onClick={() => activeRegist()}
          >
            Авторизируйся!
          </NavLink>
        </div>
      </form>
    </div>
  );
}

export default Registration;
