const defaultState = {
  items: {
    name: ["КОЛЬЦО"],
    brand: ["jow’s, Cartier"],
    title: ["Белое золото, бриллианты"],
    price: ["674 000 ₽"],
    id: []
  }
  };

  const ITEMS = "PAGEWOM";

  export const Jewelry = (state = defaultState, action) => {
    switch (action.type) {
      case ITEMS:
        return { ...state, page: action.payload };
      default:
        return state;
    }
  };

  export const itemsAction = (payload) => ({ type: ITEMS, payload });
