const defaultState = {
  from: 'add'
  };

  const FILTR_FROM = "FILTR_FROM";

  export const Jewelry = (state = defaultState, action) => {
    switch (action.type) {
      case FILTR_FROM:
        return { ...state, from: action.payload };
      default:
        return state;
    }
  };

  export const fromFiltrAction = (payload) => ({ type: FILTR_FROM, payload });
