const defaultState = {
    burger: true,
  }

const ACTIVE = "ACTIVE"
const OFF = "OFF"

export  const Burger = (state = defaultState, action) => {
    switch(action.type){
      case ACTIVE:
        return {...state, burger: true}
      case OFF:
        return {...state, burger: false}
      default:
        return state;
    }
  }

export const activeBorgerAction = () =>({type: ACTIVE, payload})
export const ofBorgerAction = () =>({type: OFF, payload})
